import axios from 'axios';
import ytSearch from 'yt-search';

const delayTyping = async (client, jid, loadingText = "·¥ò·¥è·¥ò·¥ã…™·¥Ö …™s  ü·¥è·¥Ä·¥Ö…™…¥…¢üòä...") => {
  await client.sendPresenceUpdate("composing", jid);
  await client.sendMessage(jid, {
    text: loadingText,
    contextInfo: {
      forwardingScore: 999,
      isForwarded: true,
      forwardedNewsletterMessageInfo: {
        newsletterName: "·¥ò·¥è·¥ò·¥ã…™·¥Ö …¢ ü·¥á",
        newsletterJid: "120363420342566562@newsletter"
      }
    }
  }, {
    ephemeralExpiration: 86400
  });
};

const handleMediaCommand = async (msg, client, format = "mp3") => {
  const command = msg.body.startsWith('.') 
    ? msg.body.slice(1).split(" ")[0].toLowerCase() 
    : '';
  const query = msg.body.slice(1 + command.length).trim();
  const chatId = msg.from;

  const newsletterContext = {
    forwardingScore: 999,
    isForwarded: true,
    forwardedNewsletterMessageInfo: {
      newsletterName: "·¥ò·¥è·¥ò·¥ã…™·¥Ö …¢ ü·¥á",
      newsletterJid: "120363420342566562@newsletter"
    }
  };

  if (!query) {
    return client.sendMessage(chatId, {
      text: `‚ùó *Usage:* \`.${command} <title>\`\nüìå *Example:* \`.${command} faded alan walker\``,
      contextInfo: newsletterContext
    }, { quoted: msg });
  }

  try {
    await delayTyping(client, chatId);

    const searchResult = await ytSearch(query);
    const video = searchResult.videos[0];

    if (!video) {
      return client.sendMessage(chatId, {
        text: "‚ùå *No video found for your search.*",
        contextInfo: newsletterContext
      }, { quoted: msg });
    }

    const videoId = video.videoId;
    const videoUrl = `https://www.youtube.com/watch?v=${videoId}`;
    let apiUrl, responseData;

    if (format === 'mp3') {
      apiUrl = `https://noobs-api.top/dipto/ytDl3?link=${encodeURIComponent(videoId)}&format=${format}`;
    } else {
      apiUrl = `https://jawad-tech.vercel.app/download/ytmp3?url=${encodeURIComponent(videoUrl)}`;
    }

    const response = await axios.get(apiUrl);
    responseData = response.data;

    if (!responseData.downloadLink) {
      return client.sendMessage(chatId, {
        text: "‚ùå *Error: the download link is empty or invalid.*",
        contextInfo: newsletterContext
      }, { quoted: msg });
    }

    const title = video.title;
    const author = video.author.name;
    const duration = video.timestamp;
    const published = video.ago;
    const views = video.views.toLocaleString();
    const formatted = format.toUpperCase();
    const cleanFileName = title.replace(/[\\/:*?"<>|]/g, '') + '.' + format;

    const caption = `
‚îÅ‚ùç *${formatted === "MP3" ? "SONG" : "VIDEO"}*‚îÅ
üéµ *Title:* ${title}
üë§ *Artist:* ${author}
‚è±Ô∏è *Duration:* ${duration}
üìÖ *Published:* ${published}
üëÅÔ∏è *Views:* ${views}
üì• *Format:* ${formatted}
‚îÅ‚ùç Preparing... ‚è≥‚îÅ
ü§ñ *ùìüùì∏ùìπùì¥ùì≤ùì≠*
    `.trim();

    // Send thumbnail with song/video details
    await client.sendMessage(chatId, {
      image: { url: video.thumbnail },
      caption: caption,
      contextInfo: newsletterContext
    }, { quoted: msg });

    // Send media (audio or video)
    const mediaPayload = format === "mp3"
      ? {
          audio: { url: responseData.downloadLink },
          mimetype: 'audio/mpeg',
          fileName: cleanFileName,
          contextInfo: newsletterContext
        }
      : {
          video: { url: responseData.downloadLink },
          mimetype: "video/mp4",
          fileName: cleanFileName,
          contextInfo: newsletterContext
        };

    await client.sendMessage(chatId, mediaPayload, { quoted: msg });

  } catch (err) {
    console.error(`[${format.toUpperCase()}] ERROR:`, err.message);
    await client.sendMessage(chatId, {
      text: `‚ùå *An error occurred: ${err.message}*`,
      contextInfo: newsletterContext
    }, { quoted: msg });
  }
};

const mediaHandler = async (msg, client) => {
  const command = msg.body.startsWith('.') 
    ? msg.body.slice(1).split(" ")[0].toLowerCase() 
    : '';

  switch (command) {
    case 'play':
    case 'music':
    case 'song':
    case 'audiofile':
    case 'mp3doc':
      return handleMediaCommand(msg, client, "mp3");

    case 'video':
    case 'vid':
    case 'mp4':
    case 'movie':
      return handleMediaCommand(msg, client, "mp4");
  }
};

export const aliases = [
  "play", "music", "song", "audiofile", "mp3doc",
  "video", "vid", "mp4", "movie"
];

export default mediaHandler;
